function [e,phipath,xpathOut,ypathOut] = fcn(x, y, pathX, pathY, pathPHI, datacount)
%#codegen
% path=reshape(pathV,[],3);
% x1 = pathX;
% y1 = pathY;
% phi1=pathPHI;
% 
% persistent oldPhi
% if isempty(oldPhi)
%       oldPhi = 0;
% end


etmp = inf;
e = inf;
index=1;
% find the nearest x,y on the path
for i=2:datacount
   etmp = sqrt((x-pathX(i))^2+(y-pathY(i))^2);
   if( etmp < e )
       e = etmp;
       index=i;
   end
end

xpathOut=pathX(index);
ypathOut=pathY(index);
phipath=pathPHI(index);

ex1 = (pathX(index)-x);
ey1 = (pathY(index)-y);
ex2 = cos(phipath);
ey2 = sin(phipath);
sinnnn = ex1*ey2-ey1*ex2;
e = sign(sinnnn)*e;

function [X, Y,PHI,datacount] = fcn(Points, currentlength, receivedLength)
%#codegen


X = zeros(8000,1);
Y = zeros(8000,1);
PHI = zeros(8000,1);

%We will work on array using the current Length as, Simulink might
%truncate an arbitrarily large according to the maximum length set


for i = 1:currentlength
        %Points(i).X
        %Points(i).Y
        X(i) = Points(i).X;
        Y(i) = Points(i).Y;

        %We will calculate current headway as the instantaneous slope
        if ( i > 1)
           % atan2((Y(i) - Y(i-1)),(X(i) - X(i-1)))
           PHI(i) = atan2((Y(i) - Y(i-1)),(X(i) - X(i-1)));
        else
           PHI(i) = 0;
        end
end
X(i+1) =  Points(1).X;
Y(i+1) = Points(1).Y;
PHI(i+1) = atan2((Y(1) - Y(i)),(X(1) - X(i)));

datacount = double(currentlength+1);
end

function delta = fcn(k,e,v,phi)
%#codegen
if( abs(v) < eps )
    v = eps;
end

% if phi is greater than pi (or less than -pi), then 
% we normalize to between [-pi,pi]
if( phi < -pi )
    phi = phi+2*pi;
elseif( phi > pi )
    phi = phi-2*pi;
end

% if we are pointed the "wrong" way, then screw it, let's
% follow in the direction the path is easiest to follow
% JMS I think I have a logic error here
%if( phi < -pi/2 || phi > pi/2 )
%    phi = max(min(phi - pi,pi/2),-pi/2);
%end

delta = phi + atan2(k*e,v);

function [xs,ys,phis] = fcn(radius,start)
%#codegen
angles=linspace(-pi,pi,301);
r = radius*exp(angles*1j);
xs = real(r)-radius+start(1);
ys = imag(r)+start(2);
phis=(pi/2)+angles;

% % To visualize the angles, run this code:
% radius=3.6; start=[0 0];
% angles=linspace(-pi,pi,300);
% r = radius*exp(angles*1j);
% xs = real(r)-radius+start(1);
% ys = imag(r)+start(2);
% % since the direction of travel is in the 'x' direction, the 
% % heading (phi) matchines the angle of the circle
% phis=(pi/2)+angles;
% xends=cos(phis);yends=sin(phis);
% figure;plot(xs,ys);hold on; quiver(xs,ys,xends,yends);axis equal;hold off

function [xs,ys,phis] = fcn(len,heading)
%#codegen
r = linspace(0,len,301);
xs = r*cos(heading);
ys = r*sin(heading);
phis = heading*ones(1,length(r));

