function [e,phipath,xpathOut,ypathOut] = fcn(x,y,pathV)
%#codegen
path=reshape(pathV,[],3);
x1 = path(1,1);
y1 = path(1,2);
phi1=path(1,3);
etmp = inf;
e = inf;
index=1;
% find the nearest x,y
for i=1:length(path)
   etmp = sqrt((x-path(i,1))^2+(y-path(i,2))^2);
   if( etmp < e )
       e = etmp;
       index=i;
   end
end

xpathOut=path(index,1);
ypathOut=path(index,2);
phipath=path(index,3);

ex1 = (path(index,1)-x);
ey1 = (path(index,2)-y);
ex2 = cos(phipath);
ey2 = sin(phipath);
sinnnn = ex1*ey2-ey1*ex2;
e = sign(sinnnn)*e;
